describe('handlingResponse', () => {

    // Creates a list item for each user in the response
    it('should create a list item for each user in the response', () => {
      // Mock response data
      const users = [
        { items: { login: 'user1', avatar_url: 'avatar1', html_url: 'profile1' } },
        { items: { login: 'user2', avatar_url: 'avatar2', html_url: 'profile2' } },
        { items: { login: 'user3', avatar_url: 'avatar3', html_url: 'profile3' } }
      ];

      // Mock DOM elements
      const ul = document.createElement('ul');
      ul.id = 'user-list';
      document.body.appendChild(ul);

      // Call the handlingResponse function
      handlingResponse(users);

      // Check if the correct number of list items were created
      expect(ul.children.length).toBe(users.length);

      // Check if the list items have the correct content
      for (let i = 0; i < users.length; i++) {
        const li = ul.children[i];
        const p = li.querySelector('p');
        const avatar = li.querySelector('img');
        const profile = li.querySelector('a');

        expect(p.textContent).toBe(`Username: ${users[i].items.login}`);
        expect(avatar.src).toBe(users[i].items.avatar_url);
        expect(profile.href).toBe(users[i].items.html_url);
      }
    });


});
